//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './text-and-image.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import ScrollTrigger from "gsap/ScrollTrigger";


export const TextAndImage = () => {
  gsap.registerPlugin(ScrollTrigger);

  const textAndImageImage = useRef(".text-and-image__image");

  useEffect(() => {
    gsap.from(textAndImageImage.current, {
      scrollTrigger: {
        trigger: textAndImageImage.current,
        start: "top top",
        endTrigger: ".big-title",
        end: "top bottom",
        pinSpacing: false,
        pin: true,
        toggleActions: "play none none pause",
      },
      duration: 1.5,
    });
  }, []);


  return (
    <section className="text-and-image">
      <div className="text-and-image__image" style={{
        backgroundImage: `url("https://picsum.photos/id/356/1505/796")`
      }}>
      </div>
      <div className="container">
        <div className="row justify-content-end">
          <div className="col-8 col-lg-6 position-relative">
            <div className="text-and-image__text">
              <h1>Non autem voluptas est veniam ipsa ea deleniti suscipit. </h1>
              <p>Lorem ipsum dolor sit amet. Aut optio voluptate sit enim reprehenderit <em>Qui enim
                consequatur rerum quae
                perspiciatis aut dolor illo</em>? Qui quam dignissimos aut doloremque aliquam ea
                minus
                vero in rerum sint ut
                quaerat quibusdam rem ipsa doloremque qui aperiam obcaecati. Et reiciendis iusto
                <strong>Ut
                  molestiae in impedit
                  voluptatum id iste quis</strong> est enim laudantium est consequatur error.
              </p>
              <br />
              <br />
              <h2>Non odit ipsa aut eveniet enim aut quos culpa. </h2>
              <p>Et nostrum autem <em>Aut enim ad omnis esse</em>. In voluptatum sapiente aut quam
                blanditiis
                rem nostrum fugit aut
                consequatur quos sit velit quia aut reprehenderit vero provident sunt. Qui perspiciatis
                nisi
                aut dolore vitae in
                corporis optio. Sit quidem consequatur vel nihil veritatis et excepturi vel atque
                provident
                vel similique repellat.
              </p>
              <br />
              <br />
              <h3>Sit ducimus quia ad dolorem galisum. </h3>
              <p>Et voluptas velit est sint corporisaut voluptas. Ea facere consequatur ab minus deserunt
                <em>At facilis aut
                  reprehenderit soluta qui excepturi illo eaque fuga</em>. Ex voluptatum recusandae et
                excepturi inventore At
                aliquid facere id voluptas nostrum iure harum hic molestiae harum?
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}
