//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './parallax-two.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";


export const ParallaxTwo = ({ parallaxTwo }) => {
  gsap.registerPlugin(scrollTrigger);


  return (
    <section className="parallax-two">
      <div className="hero">
        <div className="parallax-background parallax-two--layer parallax-bg" data-depth='0.30' style={{
          backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.backgroundImage}
                )`}}>
        </div>

        <div id="parallax-image-1" className="parallax-two--layer parallax-bg" data-depth='0.60' style={{
          backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.layerOne}
                )`}}>
        </div>
        <div id="parallax-image-2" className="parallax-two--layer parallax-bg" data-depth='0.85' style={{
          backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.layerTwo}
                )`}}>
        </div>
      </div>
    </section>
  )
}
