//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './parallax-one.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import ScrollTrigger from "gsap/ScrollTrigger";


export const ParallaxOne = () => {
  gsap.registerPlugin(ScrollTrigger);

  const BigTitleElement = useRef(".big-title");

  useEffect(() => {
    gsap.from(BigTitleElement.current, {
      duration: 1.5,
      scrollTrigger: {
        trigger: ".big-title",
        start: "20% center",
        end: "80% center",
        scrub: 1,
        toggleActions: "play none none pause",
        toggleClass: "highlighted",
      }
    });
  }, []);

  return (
    <section className="parallax-element">
      <div className="container-fluid">
        <div className="row">
          <div className="layer-bg layer parallax" data-depth='6' style={{
            backgroundImage: `url("/assets/parallax/background-cloud.png")`
          }}></div>


          <div className="layer-2 layer parallax parallax-top" data-depth='2' style={{
            backgroundImage: `url("/assets/parallax/top-middle.png")`
          }}></div>

          <div className="layer-1 layer parallax parallax-top-left" data-depth='2'></div>
          <div className="layer-3 layer parallax parallax-top-right" data-depth='2'></div>
          <div className="layer-5 layer parallax parallax-bottom" data-depth='2'></div>
          <div className="layer-4 layer parallax parallax-bottom-left" data-depth='2'></div>
          <div className="layer-6 layer parallax parallax-bottom-right" data-depth='2'></div>
          <div className="layer-bg-cloud layer parallax" data-depth='2'></div>
          <div className="parallax-element__title">
            <h2>Eum ipsum deserunt ut consequatur delectus. </h2>
          </div>
        </div>
      </div>
    </section >
  )
}
