//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './parallaxTwo.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";


export const ParallaxTwo = ({ bigTitle }) => {
  gsap.registerPlugin(scrollTrigger);

  const BigTitleElement = useRef(".big-title");

  useEffect(() => {
    gsap.from(BigTitleElement.current, {
      duration: 1.5,
      scrollTrigger: {
        trigger: ".big-title",
        start: "20% center",
        end: "80% center",
        scrub: 1,
        toggleActions: "play none none pause",
        toggleClass: "highlighted",
      }
    });
  }, []);

  return (
    <section className="parallax-element-images">
      <div className="div">
        <div id="parallax-background" className="parallax-bg" data-speed=".5">
          <div className="image"></div>
        </div>
        <div id="parallax-image-1" className="parallax-bg" data-speed=".10">
          <div className="image"></div>
        </div>
        <div id="parallax-image-2" className="parallax-bg" data-speed=".20>">
          <div className="image"></div>
        </div>
      </div>
    </section>
  )
}
