//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './parallax-one.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import ScrollTrigger from "gsap/ScrollTrigger";


export const ParallaxOne = () => {
  gsap.registerPlugin(ScrollTrigger);

  const container = useRef(".parallax-element")

  useEffect(() => {
    console.log(container)
  })

  const tl = useRef(".parallax-element");

  useEffect(() => {
    tl.current = gsap.timeline({
      scrollTrigger: {
        trigger: ".parallax-element",
        start: "top top",
        end: "bottom top",
        scrub: true,
      }
    });
  }, []);


  gsap.utils.toArray(".parallax-top-left").forEach(layer => {
    const depth = layer.dataset.depth;
    const movement = -(layer.offsetWidth * depth)
    tl.to(layer, {
      x: movement,
      y: movement,
      ease: "0.1",
    }, 0)
  });


  return (
    <section className="parallax-element">
      <div ref={container} className="container-fluid">
        <div className="row">
          <div className="layer-bg layer parallax" data-depth='6' style={{
            backgroundImage: `url("/assets/images/parallax-element/background.png")`
          }}></div>
          <div className="layer-2 layer parallax parallax-top" data-depth='2' style={{
            backgroundImage: `url("/assets/images/parallax-element/top-middle.png")`
          }} ></div>
          <div className="layer-1 layer parallax parallax-top-left" data-depth='2' style={{
            backgroundImage: `url("/assets/images/parallax-element/top-left.png")`
          }} ></div>
          <div className="layer-3 layer parallax parallax-top-right" data-depth='2' style={{
            backgroundImage: `url("/assets/images/parallax-element/top-right.png")`
          }} ></div>
          <div className="layer-5 layer parallax parallax-bottom" data-depth='2' style={{
            backgroundImage: `url("/assets/images/parallax-element/bottom-middle.png")`
          }} ></div>
          <div className="layer-4 layer parallax parallax-bottom-left" data-depth='2' style={{
            backgroundImage: `url("/assets/images/parallax-element/bottom-left.png")`
          }} ></div>
          <div className="layer-6 layer parallax parallax-bottom-right" data-depth='2' style={{
            backgroundImage: `url("/assets/images/parallax-element/bottom-right.png")`
          }} ></div>
          <div className="layer-bg-cloud layer parallax" data-depth='2' style={{
            backgroundImage: `url("/assets/images/parallax-element/bg-cloud.png")`
          }} ></div>


          <div className="parallax-element__title">
            <h2>Eum ipsum deserunt ut consequatur delectus.</h2>
          </div>
        </div>
      </div>
    </section >
  )
}
