//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './video-scrub.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";



export const VideoScrub = ({ video }) => {
  gsap.registerPlugin(scrollTrigger);

  const coolVideo = document.querySelector("video");

  let tl = gsap.timeline({
    scrollTrigger: {
      trigger: "video",
      start: "top top",
      end: "bottom+=200% bottom",
      scrub: true,
      markers: true
    }
  });

  // wait until video metadata is loaded, so we can grab the proper duration before adding the onscroll animation. Might need to add a loader for loonng videos
  coolVideo.onloadedmetadata = function () {
    tl.to(coolVideo, { currentTime: coolVideo.duration });
  };

  // Dealing with devices
  function isTouchDevice() {
    return (
      "ontouchstart" in window ||
      navigator.maxTouchPoints > 0 ||
      navigator.msMaxTouchPoints > 0
    );
  }
  if (isTouchDevice()) {
    coolVideo.play();
    coolVideo.pause();
  }


  return (
    <section className="video-scrub">
      <div className="container">
        <div className="row justify-content-end mb-5 justify-content-end">
          <div className="col-10 col-md-6">
            {/* <video className="video" ref={videoRef} width="100%" autoPlay muted playsInline>
              <source src={video.source} type="video/mp4" />
            </video> */}
            <video class="video" playsinline="true" webkit-playsinline="true" preload="auto" muted="muted">
              <source src="https://www.dropbox.com/s/ob3iz14tgo29qns/Abstract%20-%2020072_960.mp4?raw=1" type="video/mp4" />
            </video>
          </div>
        </div>
      </div>
    </section>
  )
}
