import { gsap } from "gsap";
import { useEffect, useRef } from "react";
import './header.scss';

export const Header = ({ header }) => {
  const headerBackground = useRef(".header__background");
  const headerFirstLine = useRef(".header__inner-line");


  useEffect(() => {
    gsap.to(headerBackground.current, {
      scale: 1.1,
      rotationZ: 0.01,
      duration: 3,
      opacity: 1,
      scrub: true,
      markers: true
    });
  }, []);
  useEffect(() => {
    gsap.from(headerFirstLine.current, {
      rotation: 0.01,
      duration: .5,
      opacity: 0,
      delay: 1,
      y: '50%',
      ease: "power2.out",
      stagger: .2
    });
  });



  return (
    <section className="header">
      <div className="box">Hello</div>
      <div className="header__background"></div>
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-8">
            <div className="header__title">
              <h1>
                <div className="header__line">
                  <div className="header__inner-line">
                    {header.titleFirstLine}
                  </div>
                </div>
                <div className="line">
                  <div className="header__inner-line">
                    {header.titleSecondLine}
                  </div>
                </div>
              </h1>
              <p className="header__body-text"> {header.bodyText}
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}
