//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './video-scrub.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";



export const VideoScrub = ({ video }) => {
  gsap.registerPlugin(scrollTrigger);

  const videoRef = document.querySelector(".video");

  videoRef.pause();
  videoRef.currentTime = 0;

  gsap.fromTo(videoRef, { currentTime: 3 * i }, {
    scrollTrigger: {
      trigger: step,
      scrub: 2,
      start: "top bottom",
      end: "bottom bottom",
    },
    currentTime: 3 * (i + 1),
    duration: 1,
    ease: "none",
  });

  useEffect(() => {
    console.log(videoRef);
  })

  return (
    <section className="video-scrub">
      <div className="container">
        <div className="row justify-content-end mb-5 justify-content-end">
          <div className="col-10 col-md-6">
            <video className="video" ref={videoRef} width="100%" autoPlay muted playsInline>
              <source src={video.source} type="video/mp4" />
            </video>
          </div>
        </div>
      </div>
    </section>
  )
}
