import { gsap } from "gsap";
import { useEffect, useRef } from "react";
import './iphone-mockup.scss';
import iphoneImage from './iphone.png'

export const IphoneMockup = ({ iphone }) => {
  const componentRef = useRef();

  useEffect(() => {
    gsap.to(componentRef.current, {
      rotation: "+=360"
    });
  });


  return (
    <section className="iphone-mockup">
      <div className="iphone-mockup__circle" ref={componentRef}></div>
      <img className="iphone-mockup__image" src={iphoneImage} alt="" />
      <div className="iphone-mockup__text">
        <h2>{iphone.title}</h2>
        <p>{iphone.subTitle}</p>
      </div>
    </section>
  )
}
