//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './parallax-two.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";


export const ParallaxTwo = ({ parallaxTwo }) => {
  gsap.registerPlugin(scrollTrigger);

  const tl = gsap.timeline({
    scrollTrigger: {
      trigger: ".parallax-two",
      start: "top top",
      end: "bottom top",
      scrub: true,
      markers: true
    }
  });

  useEffect(() => {
    gsap.utils.toArray(".parallax").forEach(layer => {
      const depth = layer.dataset.depth;
      const movement = -(layer.offsetWidth * depth)
      tl.to(layer, {
        x: movement,
        y: movement,
        ease: "0.1",
      }, 0)
    });
  });



  return (
    <section className="parallax-two">
      <div className="hero">
        <div className="parallax-background parallax-two--layer parallax-element parallax-bg"
          data-depth='0.30' style={{
            backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.backgroundImage}
                )`}}>
        </div>

        <div className="parallax-image-1 parallax-two--layer parallax-element parallax-bg"
          data-depth='0.60' style={{
            backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.layerOne}
                )`}}>
        </div>
        <div className="parallax-image-2 parallax-two--layer parallax-element parallax-bg"
          data-depth='0.85' style={{
            backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.layerTwo}
                )`}}>
        </div>
      </div>
    </section>
  )
}
