//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './big-title.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import ScrollTrigger from "gsap/ScrollTrigger";


export const BigTitle = ({ bigTitle }) => {
  gsap.registerPlugin(ScrollTrigger);

  const BigTitleElement = useRef(".big-title");

  useEffect(() => {
    gsap.from(BigTitleElement.current, {
      duration: 1.5,
      scrollTrigger: {
        trigger: ".big-title",
        start: "20% center",
        end: "80% center",
        scrub: 1,
        toggleActions: "play none none pause",
        toggleClass: "highlighted",
      }
    });
  }, []);



  return (
    <section className="big-title">
      <div className="container">
        <div className="row">
          <div className="col-6">
            <h2 className="big-title__title">Non autem voluptas <span className="big-title__title--highlight">est
              veniam
              ipsa ea</span>
              deleniti suscipit.
            </h2>
          </div>
          <div className="col-5 offset-1">
            <p className="big-title__text"> {bigTitle}
            </p>
          </div>
        </div>
      </div>
    </section>
  )
}
