//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './images-flying-in.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import ScrollTrigger from "gsap/ScrollTrigger";


export const ImagesFlyingIn = () => {
  gsap.registerPlugin(ScrollTrigger);

  const imageLeft = useRef(".image-animate-left");
  const imageRight = useRef(".image-animate-right");

  useEffect(() => {
    gsap.from(imageLeft.current, {
      scrollTrigger: {
        trigger: ".images-flying-in",
        start: "top center",
        end: "bottom center",
        scrub: 1,
        toggleActions: "restart none reverse none",
      },
      x: '-200px',
      y: '200px',
      duration: 1.2,
    });
  }, []);

  useEffect(() => {
    gsap.from(imageRight.current, {
      scrollTrigger: {
        trigger: ".images-flying-in",
        start: "top center",
        end: "bottom center",
        scrub: 1,
        toggleActions: "restart none reverse none",
      },
      x: '200px',
      y: '-200px',
      ease: Sine.easeInOut,
      duration: 1.2,
    });
  }, []);

  return (
    <section className="text-flying-in">
      <div className="container">
        <div className="row justify-content-end mb-5 justify-content-end">
          <div className="col-6">
            <div className="text-animate-left">
              <h4>
                {textFlyingIn[0].title}
              </h4>
              <p className="">{textFlyingIn[0].body}</p>
            </div>
          </div>
        </div>

        <div className="row row justify-content-start">
          <div className="col-6">
            <div className="text-animate-right">
              <h4>
                {textFlyingIn[1].title}
              </h4>
              <p className="">{textFlyingIn[1].body}</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}
