import { gsap } from "gsap";
import { useEffect, useRef } from "react";
import './iphone-mockup.scss';
import iphoneImage from './/assets/iphone.png'

export const IphoneMockup = () => {
  const boxRef = useRef();

  // wait until DOM has been rendered
  useEffect(() => {
    gsap.to(boxRef.current, {
      rotation: "+=360"
    });
  });


  return (
    <section className="iphone-mockup">
      <div className="iphone-mockup__circle"></div>
      <img className="iphone-mockup__image" src={iphoneImage} alt="" />
      <div className="iphone-mockup__text">
        <h2>De nieuwe iPhone</h2>
        <p>Vanaf â‚¬ 1.159 zonder inruil*</p>
      </div>
    </section>
  )
}
