import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './parallax-two.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";


export const ParallaxTwo = ({ parallaxTwo }) => {
  gsap.registerPlugin(scrollTrigger);

  // const tl = gsap.timeline({
  //   scrollTrigger: {
  //     trigger: ".parallax-element-images",
  //     start: "top top",
  //     end: "bottom top",
  //     scrub: true,
  //     markers: true
  //   }
  // })

  // useEffect(() => {
  //   gsap.utils.toArray(".parallax-two").forEach(layer => {
  //     const depth = layer.dataset.depth;
  //     const movement = -(layer.offsetHeight * depth)
  //     tl.to(layer, { y: movement, ease: "none" }, 0)
  //   }
  //   });

  return (
    <section className="parallax-element-images">
      <div className="div">
        <div id="parallax-background" className="parallax-two parallax-bg" data-speed=".5" data-depth='0.10'>
          <div className="image" style={{
            backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.backgroundImage}
                )`}}></div>
        </div>
        <div id="parallax-image-1" className="parallax-two parallax-bg" data-speed=".10" data-depth='0.10'>
          <div className="image" style={{
            backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.layerOne}
                )`}}></div>
        </div>
        <div id="parallax-image-2" className="parallax-two parallax-bg" data-speed=".20>" data-depth='0.10'>
          <div className="image" style={{
            backgroundImage: `url( 
                ${process.env.PUBLIC_URL + parallaxTwo.layerTwo}
                )`}}></div>
        </div>
      </div>
    </section>
  )
}
