//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './video-scrub.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";



export const VideoScrub = () => {
  gsap.registerPlugin(scrollTrigger);

  const video = document.querySelector(".video-background");
  let src = "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4";


  const tl = gsap.timeline({
    defaults: { duration: 1 },
    scrollTrigger: {
      trigger: "#container",
      start: "top top",
      end: "bottom bottom",
      scrub: true
    }
  });

  useEffect(() => {
    tl.fromTo(
      video,
      {
        currentTime: 0
      },
      {
        currentTime: video.duration || 1
      }
    );
    setTimeout(function () {
      if (window["fetch"]) {
        fetch(src)
          .then((response) => response.blob())
          .then((response) => {
            var blobURL = URL.createObjectURL(response);

            var t = video.currentTime;
            once(document.documentElement, "touchstart", function (e) {
              video.play();
              video.pause();
            });

            video.setAttribute("src", blobURL);
            video.currentTime = t + 0.01;
          });
      }
    }, 1000);
  });




  console.log(video.source);
  return (
    <section className="video-scrub">
      <div id="container">
        <div className="row justify-content-end mb-5 justify-content-center">
          <div className="col-12">
            <video src={video.source} playsinline="true" webkit-playsinline="true" preload="auto" muted="muted" class="video-background" >
            </video>
          </div>
        </div>
      </div>
    </section>
  )
}
