//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling ---------------------------------------
import './video-scrub.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import scrollTrigger from "gsap/ScrollTrigger";



export const VideoScrub = ({ video }) => {
  gsap.registerPlugin(scrollTrigger);




  const tl = gsap.timeline({
    defaults: { duration: 1 },
    scrollTrigger: {
      trigger: "#container",
      start: "top top",
      end: "bottom bottom",
      scrub: true
    }
  });

  useEffect(() => {
    tl.fromTo(
      video,
      {
        currentTime: 0
      },
      {
        currentTime: video.duration || 1
      }
    );
  });



  console.log(video.source);
  return (
    <section className="video-scrub">
      <div id="container">
        <div className="row justify-content-end mb-5 justify-content-center">
          <div className="col-12">
            <video src={video.source} playsinline="true" webkit-playsinline="true" preload="auto" muted="muted" class="video-background" >
            </video>
          </div>
        </div>
      </div>
    </section>
  )
}
