//React -----------------------------------------
import { useEffect, useRef } from "react";

//styling and assets ----------------------------
import iphoneImage from './iphone.png'
import './iphone-mockup.scss';

//gsap ------------------------------------------
import { gsap } from "gsap"
import ScrollTrigger from "gsap/ScrollTrigger";




export const IphoneMockup = ({ iphone }) => {
  gsap.registerPlugin(ScrollTrigger);

  const iPhoneMockupImage = useRef(".iphone-mockup__image");
  const iPhoneMockupCicle = useRef(".iphone-mockup__circle");
  const iPhoneMockupText = useRef(".iphone-mockup__text");

  useEffect(() => {
    gsap.to(iPhoneMockupImage.current, {
      x: 200,
      duration: 2,
      scrollTrigger: {
        trigger: ".iphone-mockup",
        start: "20% center",
        end: "center center",
        toggleActions: "restart none reverse none",
        scrub: 1,
      }
    });
  }, []);

  useEffect(() => {
    gsap.to(iPhoneMockupCicle.current, {
      scale: 24,
      x: "43vw",
      duration: 6,
      scrollTrigger: {
        trigger: ".iphone-mockup",
        start: "20% center",
        end: "-20% center",
        toggleActions: "restart none reverse none",
        scrub: 1.5,
      }
    });
  }, []);

  useEffect(() => {
    gsap.to(iPhoneMockupText.current, {
      x: 450,
      duration: 1,
      opacity: 1,
      scrollTrigger: {
        trigger: ".iphone-mockup",
        start: "30% center",
        end: "center center",
        toggleActions: "restart none reverse none",
        scrub: 1,
      }
    });
  }, []);


  return (
    <section className="iphone-mockup">
      <div className="iphone-mockup__circle" ></div>
      {/* <img className="iphone-mockup__image" ref={iPhoneMockupImage} src={iphoneImage} alt="" /> */}
      <img className="iphone-mockup__image" src={iphoneImage} alt="" />
      <div className="iphone-mockup__text">
        <h2>{iphone.title}</h2>
        <p>{iphone.subTitle}</p>
      </div>
    </section>
  )
}
